$port = 3000; $token = "secret-token-123"; $csvPath = "backend\test_orders.csv"; Write-Host "`n--- Checking /health ---"; Invoke-RestMethod -Uri "http://localhost:$port/health" | ConvertTo-Json; Write-Host "`n--- Checking /orders (initial) ---"; Invoke-RestMethod -Uri "http://localhost:$port/orders?limit=5" | ConvertTo-Json; Write-Host "`n--- Testing /tax/calculate-tax ---"; $taxBody = @{ lat = 40.7128; lon = -74.006; subtotal = 100 } | ConvertTo-Json; Invoke-RestMethod -Method Post -Uri "http://localhost:$port/tax/calculate-tax" -ContentType "application/json" -Body $taxBody | ConvertTo-Json; Write-Host "`n--- Testing /import/orders/import (Note: multipart/form-data) ---"; Write-Host "Run this curl command manually to test import:"; Write-Host "curl -X POST -H \"Authorization: Bearer $token\" -F \"file=@$csvPath\" http://localhost:$port/import/orders/import"; try { $response = Invoke-RestMethod -Uri "http://localhost:$port/health"; Write-Host "`nServer is UP." } catch { Write-Host "`nServer is DOWN. Please start it with 'npm start' in the backend folder." }